import hywt.mandel.*;
import hywt.mandel.numtype.DeepComplex;
import hywt.mandel.numtype.FloatExp;

import javax.imageio.ImageIO;
import java.awt.image.BufferedImage;
import java.io.File;
import java.io.IOException;
import java.math.BigDecimal;

public class Main {
    public static void main(String[] args) throws IOException {
        BigDecimal re = new BigDecimal("-1.");
        BigDecimal im = new BigDecimal("0.");
        FloatExp scale = FloatExp.fromDouble(4).div(FloatExp.parseFloatExp("1.5710523668200003e671"));
        long iterations = 22067;

        Parameter p = new Parameter(new DeepComplex(re, im), scale, iterations);
        Configuration config = new Configuration(p, new File("mmm_out"));

        RenderManager man = new RenderManager(config);
        man.save();

        man.start();

    }
}